import React, { Component } from "react";
import {
  getAmount2,
  changeAmount1,
  setCurrency1,
  setCurrency2,
  swap
} from "../actions/actions";
import { connect } from "react-redux";

class Exchange extends Component {
  // handleChange = e => {
