  setCurrency2 = e => {
    // this.setState({
    //   c2: e.target.value
    // });
  };

  Swich = () => {
    // this.setState({
    //   c1: this.state.c2,
    //   c2: this.state.c1
    // });
  };

  componentDidMount() {
    this.props.getAmount2();
  }

  componentDidUpdate(prevProps, prevState) {
    // only update chart if the data has changed
    if (prevProps !== this.props) {
      this.props.getAmount2();
    }
  }

  render() {
    const { amount1, amount2, c1, c2 } = this.props.currency;
    return (
      <div>
        <select onChange={this.props.setCurrency1} id="currency-one">
          <option value="USD">{c1}</option>
          <option value="CNY">{c2}</option>
        </select>
        <input
          type="number"
          placeholder="number"
          value={this.props.currency.amount1}
          onChange={this.props.changeAmount1}
        />

        <div className="swap-rate-container">
          <button className="btn" id="swap" onClick={this.props.swap}>
            Swap
          </button>
          <div className="rate" id="rate"></div>
        </div>
        <select onChange={this.props.setCurrency2} id="currency-one">
          <option value="CNY">{c2}</option>
          <option value="USD">{c1}</option>
        </select>
        {/* {this.state.amount1},
                {this.state.amount2} */}

        <input
          type="number"
          placeholder="0"
          value={this.props.currency.amount2}
        />
      </div>
    );
  }
}

const mapStateToProps = state => ({
  currency: state.currency
});

export default connect(mapStateToProps, {
  getAmount2,
  changeAmount1,
  setCurrency1,
  setCurrency2,
  swap
})(Exchange);
